import React, { useState } from "react";

function CreateArea() {

  const [note, setNote] = useState({
    title: "",
    content: ""
  })

  function hadleChange(event){
    const {name, value} = event.target;
    setNote(preValue => {
      return{
        ...preValue,
        [name]: value
      }
    })
  }

  return (
    <div>
      <form>
        <input name="title" onChange={hadleChange} placeholder="Title" value = {note.title}/>
        <textarea name="content" onChange={hadleChange} placeholder="Take a note..." rows="3" value = {note.content}/>
        <button>Add</button>
      </form>
    </div>
  );
}

export default CreateArea;
